package ws.gac.secure;import org.springframework.web.bind.annotation.*;import org.springframework.jdbc.core.JdbcTemplate;import java.util.List;@RestController public class UserController { private final JdbcTemplate jdbc; public UserController(JdbcTemplate jdbc){ this.jdbc = jdbc; } @GetMapping("/search") public List<String> search(@RequestParam String q){ String sql = "SELECT name FROM users WHERE name LIKE ?"; return jdbc.query(sql, ps -> ps.setString(1, "%" + q + "%"), (rs, i) -> rs.getString(1)); } }