package ws.gac.secure;import jakarta.annotation.PostConstruct;import org.springframework.context.annotation.Configuration;import org.springframework.jdbc.core.JdbcTemplate;@Configuration public class WebConfig { private final JdbcTemplate jdbc; public WebConfig(JdbcTemplate jdbc){ this.jdbc = jdbc; } @PostConstruct public void init(){ jdbc.execute("CREATE TABLE users(id INT AUTO_INCREMENT PRIMARY KEY, name VARCHAR(64))"); jdbc.execute("INSERT INTO users(name) VALUES ('alice'),('bob'),('carol')"); } }